-- ----------------------------
-- Table structure for vb_menu
-- ----------------------------
DROP TABLE IF EXISTS `vb_menu`;
CREATE TABLE `vb_menu` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `path` varchar(50) DEFAULT NULL COMMENT '路径',
  `component` varchar(50) DEFAULT NULL COMMENT '需要的组件',
  `name` varchar(50) NOT NULL COMMENT '菜单名称',
  `iconCls` varchar(20) DEFAULT NULL COMMENT '图标',
  `ismenu` int(11) NOT NULL COMMENT '是否是菜单（指的是在首页是不是菜单1：是；0：不是）',
  `keepAlive` int(11) NOT NULL DEFAULT '0' COMMENT '是否keepAlive',
  `requireAuth` int(11) NOT NULL DEFAULT '1' COMMENT '是否需要验证',
  `pids` varchar(200) DEFAULT NULL COMMENT '该字段所有的父级id',
  `parentid` int(11) DEFAULT NULL COMMENT '父级id',
  `delete` int(11) DEFAULT '0' COMMENT '是否删除',
  `enabled` int(11) NOT NULL DEFAULT '1' COMMENT '是否激活',
  `url` varchar(50) DEFAULT NULL COMMENT '前端请求后端的url',
  `method` varchar(20) DEFAULT NULL COMMENT 'rest风格请求方法',
  PRIMARY KEY (`id`),
  KEY `vb_menu_parentid` (`parentid`),
  CONSTRAINT `vb_menu_parentid` FOREIGN KEY (`parentid`) REFERENCES `vb_menu` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8mb4 COMMENT='菜单表';

